import React, { useRef, useState, useEffect } from "react";
import transformerImages from "./TransformerImageList.json";

export default function ImageUpload({ transformerNo, inspectionNo, type = "Thermal" }) {
  const [uploading, setUploading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [uploadFile, setUploadFile] = useState(null);
  const [images, setImages] = useState([]);
  const [weather, setWeather] = useState("Sunny");

  const fileInputRef = useRef(null);
  const uploadInterval = useRef(null);

  // Load existing images
  useEffect(() => {
    const transformer = transformerImages[transformerNo];
    if (!transformer) return;

    if (type === "Baseline") {
      setImages(transformer.Baseline ? [transformer.Baseline] : []);
    } else if (type === "Thermal") {
      const inspection = transformer.Inspections?.[inspectionNo];
      setImages(inspection?.Thermal ? [inspection.Thermal] : []);
    }
  }, [transformerNo, inspectionNo, type]);

  const handleClick = () => fileInputRef.current.click();

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setUploadFile(file);
    setUploading(true);
    setProgress(0);

    uploadInterval.current = setInterval(() => {
      setProgress((prev) => {
        if (prev >= 100) {
          clearInterval(uploadInterval.current);
          setUploading(false);

          const now = new Date();
          const uploadedData = [file.name, now.toLocaleDateString(), now.toLocaleTimeString()];
          if (type === "Thermal") uploadedData.push(weather);

          const updatedImages = [uploadedData];
          setImages(updatedImages);

          // Update JSON structure
          if (!transformerImages[transformerNo]) transformerImages[transformerNo] = { Baseline: [], Inspections: {} };

          if (type === "Baseline") {
            transformerImages[transformerNo].Baseline = uploadedData;
          } else if (type === "Thermal") {
            if (!transformerImages[transformerNo].Inspections) transformerImages[transformerNo].Inspections = {};
            if (!transformerImages[transformerNo].Inspections[inspectionNo]) transformerImages[transformerNo].Inspections[inspectionNo] = {};
            transformerImages[transformerNo].Inspections[inspectionNo].Thermal = uploadedData;
          }

          return 100;
        }
        return prev + 1;
      });
    }, 50);
  };

  const handleCancel = () => {
    clearInterval(uploadInterval.current);
    setUploading(false);
    setProgress(0);
    setUploadFile(null);
  };

  const getImageSrc = (imageName) => {
    try {
      return require(`./${imageName}`);
    } catch (err) {
      console.error("Image not found:", imageName);
      return "";
    }
  };

  const hasImage = images.length > 0 && images[0][0];

  return (
    <div className="bg-white rounded-2xl shadow p-6 flex flex-col gap-4 w-full max-w-md">
      {uploading ? (
        <>
          <div className="flex flex-col items-center justify-center h-full">
            <h3 className="text-2xl font-bold text-gray-800 mb-4">{type} Image Uploading</h3>
            <p className="text-gray-600 mb-4">{type} image is being uploaded.</p>
          </div>
          <div className="mb-2 flex justify-end">
            <span className="text-sm text-gray-700 font-medium">{progress}%</span>
          </div>
          <div className="w-full bg-indigo-200 rounded-full h-4 overflow-hidden mb-4">
            <div className="bg-indigo-600 h-4 transition-all duration-200" style={{ width: `${progress}%` }} />
          </div>
          <button
            className="bg-white text-gray-600 px-4 py-2 rounded border border-gray-400 hover:bg-gray-100 w-auto"
            onClick={handleCancel}
          >
            Cancel
          </button>
        </>
      ) : !hasImage ? (
        <>
          <h3 className="text-2xl font-bold text-gray-800 mb-4">{type} Image</h3>
          {type === "Thermal" && (
            <>
              <label className="text-lg font-bold text-gray-800 mb-2 block">Weather Condition</label>
              <select
                value={weather}
                onChange={(e) => setWeather(e.target.value)}
                className="border rounded px-3 py-2 mb-4 w-full"
              >
                <option value="Sunny">Sunny</option>
                <option value="Cloudy">Cloudy</option>
                <option value="Rainy">Rainy</option>
              </select>
            </>
          )}
          <button
            className="w-full bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
            onClick={handleClick}
          >
            Upload {type} Image
          </button>
          <input type="file" ref={fileInputRef} className="hidden" onChange={handleFileChange} />
        </>
      ) : (
        <p className="text-gray-600">Image already exists. You can upload a new one if needed.</p>
      )}
    </div>
  );
}
